import { Component, OnInit } from '@angular/core';
import { Chart, Colors } from 'chart.js/auto';
import { BalanceStatistics } from '../balanceStatistics/balance-statistics';
import { BalanceStatisticsService } from '../balanceStatistics/balance-statistics.service';

@Component({
  selector: 'app-graph',
  templateUrl: './graph.component.html',
  styleUrls: ['./graph.component.css'],
})
export class GraphComponent implements OnInit{
  balance: BalanceStatistics[] = [];

  constructor(private balanceService: BalanceStatisticsService) { }


  ngOnInit() {
    this.balanceService.findAll().subscribe(data => {
      this.balance = data;
    });
    console.log(typeof this.balance[0].balance);


    setTimeout(() => {
      var myChart = new Chart('ctx', {
        type: 'bar',
        data: {
          labels: ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"],
          datasets: [{
            data: this.balance[0].balance,
            borderWidth: 2,
            borderColor: 'white',
            backgroundColor: 'white',
          }]
        },
        options: {
          scales: {
            x: {
              suggestedMin: 20,
              ticks: { color: 'white'},
              grid: {
                color: 'white'
              }
            },
            y: {
              ticks: { color: 'white'},
              grid: {
                color: 'white'
              }
            },
          }
        }

      });
      var myCharts = new Chart('cts', {
        type: 'bar',
        data: {
          labels: ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"],
          datasets: [{
            data: [+1200, +300, +500, -1300, +300, +340, +248],
            borderWidth: 2,
            borderColor: 'white',
            backgroundColor: 'white',

          }]
        },
        options: {
          scales: {
            x: {
              suggestedMin: 20,
              ticks: { color: 'white'},

              grid: {
                color: 'white'
              }
            },
            y: {
              ticks: { color: 'white'},
              grid: {
                color: 'white'
              }
            }
          },
        }
      });
    });
  }
  print() {
    console.log(this.balance[0].getBalance());
  }

}
<div id="divChart" style="margin-top:7%; min-height: 100px; margin-bottom: 7%; color: white">
    Your balance in each day {{balance[0].balance}}
    <canvas id="ctx" name="ctx" style="height: 100%; position: relative;"></canvas>
    Your incomes-outcomes in each day
    <canvas id="cts" name="cts" style="position: relative;"></canvas>
    {{balance[0].getBalance()}}
</div>
