import { Component, OnInit } from '@angular/core';
import { Chart } from 'chart.js';
import { Account } from './account';
import { AccountServiceService } from './account-service.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{
  title = 'OurBank';
  name = "Isaac Newton";
  accounts: Account[];

  getName() {
    return this.accounts[0].name;
  }

  constructor(private accountService: AccountServiceService) { }

  ngOnInit(): void {
    this.accountService.findAll().subscribe(data => {
      this.accounts = data;
    });
  }
}
<nav class="navbar navbar-expand-lg container">
    <h1 class="navbar-brand">OurBank</h1>
    <h3 style="color: white; margin-left: 20px;">{{getName()}}</h3>
    <button class="btn btn-primary" id="login">Login</button>
</nav>

<div class="container" style="margin-top: 3%; padding-bottom: 3%;">
  <div class="row justify-content-around">
    <div class="container col-4" style="margin-top: 7.5%; float:left;">
      <app-balance></app-balance>
    </div>
    <div class="container col-4" style="margin-top: 7.5%;">
      <app-graph></app-graph>
    </div>
  </div>
  <div class="container col-10" style="margin-top: 7.5%;">
    <app-transfer></app-transfer>
  </div>
</div>
<footer style="margin-top: 7%; text-align: center; color: white">
  <div class="container" style="padding-bottom: 1%; padding-top: 1%;"><h3>OurBank.</h3><h5>Everything for you</h5></div>
</footer>
