import { Component } from '@angular/core';

@Component({
  selector: 'app-balance',
  templateUrl: './balance.component.html',
  styleUrls: ['./balance.component.css']
})
export class BalanceComponent {
  transactions = [
    {
      type: "Cafe",
      amount: 14
    },
    {
      type: "Electonics shop",
      amount: 88
    }
  ]

  balance() {
    return 1488.99;
  }
}
