import { Component, OnInit } from '@angular/core';
import { Chart, Colors } from 'chart.js/auto';

@Component({
  selector: 'app-graph',
  templateUrl: './graph.component.html',
  styleUrls: ['./graph.component.css'],
})
export class GraphComponent implements OnInit{
  constructor() { }
  ngOnInit() {
    setTimeout(() => {
      var myChart = new Chart('ctx', {
        type: 'bar',
        data: {
          labels: ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"],
          datasets: [{
            label: 'Your balance in each day',
            data: [1200, 1500, 2000, 700, 900, 1240, 1488],
            borderWidth: 2
          }]
        },
        options: {
          scales: {
            x: {
              suggestedMin: 20,
              grid: {
                color: withLatestFrom;
              }
            }

          }
        }

      });
      var myCharts = new Chart('cts', {
        type: 'bar',
        data: {
          labels: ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"],
          datasets: [{
            label: 'your incomes-outcomes in each day',
            data: [+1200, +300, +500, -1300, +300, +340, +248],
            borderWidth: 2
          }]
        },
      });
    });
  }


}
